import React from 'react';
import axios from 'axios';
import '../assets/styles/all.css';
import '../assets/styles/updatePasswordCheck.css';

const UpdatePasswordCheck = () => {

    const readBoard = (idx) => {
        axios.get(`http://localhost:3001/read/${idx}`);
        console.log(idx, "<== guestbook (아이디 확인)");
    }

    return(
        <>
            <div className="checkAll">
                <div className="check_password">
                    <h4>비밀번호 입력 :</h4>
                    <input type="password"></input>
                    <button onClick={()=> {readBoard(value.id)}}>입력</button>
                </div>
            </div>
        </>
    )
}

export default UpdatePasswordCheck;
