import React from "react";
import propTypes from 'prop-types';
import styled from 'styled-components';
import '../assets/styles/commentDeleteModal.css';
import ModalCloseButton from './modalCloseButton';

const CommentDeleteModal = ({className, closeOn, closingMask, closing, vision, children}) => {
    const onClickMask = (e) => {
        if (e.target === e.currentTarget) {
            closeOn(e)
        }
    }

    const closes = (e) => {
        if (closeOn) {
            closeOn(e)
        }
    }
    
    return(
        <>
            <ModalOverlaying vision={vision}/>
            <ModalWrapping
            className={className}
            onClick={closingMask ? onClickMask : null}
            tabIndex="-1"
            vision={vision}>
                <ModalInnering tabIndex="0" className="modal-innering">
                    비밀번호 :<input type="password" className="passwordInputing"></input>
                    <button type="input" className="inputButton">입력</button>
                    {closing && <ModalCloseButton className="modal-closes" onClick={closes} />}
                    {children}
                </ModalInnering>
            </ModalWrapping>
        </>
    )
}

CommentDeleteModal.propTypes = {
    closing: true,
    closingMask: true,
    vision : false
}

const ModalWrapping = styled.div`
box-sizing : border-box;
display: ${(props) => (props.vision ? 'block' : 'none')};
position: fixed;
top: 0;
right: 0;
bottom: 0;
left: 0;
z-index: 1000;
overflow: auto;
outline: 0;
`

const ModalOverlaying = styled.div`
box-sizing: border-box;
display: ${(props) => (props.vision ? 'block' : 'none')};
position: fixed;
top: 0;
left: 0;
bottom: 0;
right: 0;
background-color: rgba(0, 0, 0, 0.6);
z-index: 999;
`

const ModalInnering = styled.div`
box-sizing : border-box;
position: relative;
box-shadow: 0 0 6px 0 rgba(0, 0, 0, 0.5);
background-color: #83A0D2;
color:#fff;
border-radius: 10px;
width: 360px;
max-width: 480px;
top: 50%;
transform: translateY(-50%);
margin: 0 auto;
padding: 40px 20px;
`

export default CommentDeleteModal;
