                                    <div>
                                        <div className="page_comments_text_id">
                                            <p>닉네임</p>
                                            <input></input>
                                        </div>
                                        <div className="page_comments_text_pw">
                                            <p>비밀번호</p>
                                            <input></input>
                                        </div>
                                    </div>
                                    <textarea></textarea>
                                    <button>입력</button>
                                </div>
                            </div>
                        </article>
                    </form>
        </>
    ) 
}

export default Read;
